import React, { useState } from 'react';
import { Image, TouchableHighlight, View } from 'react-native';
import useStyles from './ImageViewer.module';
import PlusSvg from '../../assets/icons/plusSvg';

export default function ImageViewer({ placeholderImageSource, selectedImage, isAvatar }) {
  const styles = useStyles();
  const [isHovered, setIsHovered] = useState(false);

  const handlePressIn = () => {
    setIsHovered(true);
  };

  const handlePressOut = () => {
    setIsHovered(false);
  };

  const imageSource = selectedImage !== null
    ? { uri: selectedImage }
    : placeholderImageSource;

  return (
    <TouchableHighlight
      onPressIn={handlePressIn}
      onPressOut={handlePressOut}
      underlayColor="transparent"
    >
      <View>
        <Image source={imageSource} style={isAvatar ? styles.avatarImage : styles.image} />
        {isAvatar && isHovered && (
          <View style={styles.overlay}>
            <PlusSvg style={styles.plusSvg} />
          </View>
        )}
      </View>
    </TouchableHighlight>
  );
}
