        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Basic ${btoa(`${username}:${password}`)}`,
        },
      });

      if (channelResponse.ok) {
        const channelData = await channelResponse.json();
        const formattedResults = [];
        const unauthResults = [];

