import React, { useState } from 'react';
import { Image, TouchableOpacity, View } from 'react-native';
import useStyles from './ImageViewer.module';
import PlusSvg from '../../assets/icons/plusSvg';

export default function ImageViewer({ placeholderImageSource, selectedImage, isAvatar }) {
  const styles = useStyles();
  const [isHovered, setIsHovered] = useState(false);

  const handleMouseEnter = () => {
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
  };

  const imageSource = selectedImage !== null
    ? { uri: selectedImage }
    : placeholderImageSource;

  return (
    <TouchableOpacity
      onPress={() => {}}
      activeOpacity={1}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      style={styles.touchableOpacity}
    >
      <View>
        <Image source={imageSource} style={isAvatar ? styles.avatarImage : styles.image} />
        {isAvatar && isHovered && (
          <View style={styles.nonePhoto}>
            <PlusSvg style={styles.plusSvg} />
          </View>
        )}
      </View>
    </TouchableOpacity>
  );
}
